import { Trans } from '@lingui/macro'
import React from 'react'
import { DndBlockItem } from '../../types'
import { getFormikKey } from '../../utils'
import TextInput from '../components/TextInput'
import { Business } from '@material-ui/icons'
import { Grid } from '@material-ui/core'
import { useCountries as getCountries } from '../../utils'
import Country from '../components/Country'

export default {
    id: 'address',
    label: <Trans>Address</Trans>,
    icon: Business,
    parent: 'form-elements',
    render: (renderProps) => {
        return (
            <Grid container alignItems="stretch" spacing={2}>
                <Grid item xs={12}>
                    {TextInput.render(
                        renderProps,
                        'address-line-1',
                        getFormikKey(renderProps, 'address-line-1')
                    )}
                </Grid>
                <Grid item xs={12}>
                    {TextInput.render(
                        renderProps,
                        'address-line-2',
                        getFormikKey(renderProps, 'address-line-2')
                    )}
                </Grid>
                <Grid item xs={6}>
                    {TextInput.render(renderProps, 'city', getFormikKey(renderProps, 'city'))}
                </Grid>
                <Grid item xs={6}>
                    {TextInput.render(renderProps, 'state', getFormikKey(renderProps, 'state'))}
                </Grid>
                <Grid item xs={6}>
                    {TextInput.render(renderProps, 'postal', getFormikKey(renderProps, 'postal'))}
                </Grid>
                <Grid item xs={6}>
                    {Country.render(renderProps, 'country', getFormikKey(renderProps, 'country'))}
                </Grid>
            </Grid>
        )
    },
    validationSchema: (renderProps: any) => {
        const schema: any = {}
        schema[getFormikKey(renderProps, 'address-line-1')] = TextInput.validationSchema?.(
            renderProps,
            'address-line-1'
        )
        schema[getFormikKey(renderProps, 'address-line-2')] = TextInput.validationSchema?.(
            renderProps,
            'address-line-2'
        )
        schema[getFormikKey(renderProps, 'city')] = TextInput.validationSchema?.(
            renderProps,
            'city'
        )
        schema[getFormikKey(renderProps, 'state')] = TextInput.validationSchema?.(
            renderProps,
            'state'
        )
        schema[getFormikKey(renderProps, 'postal')] = TextInput.validationSchema?.(
            renderProps,
            'postal'
        )
        schema[getFormikKey(renderProps, 'country')] = Country.validationSchema?.(
            renderProps,
            'country'
        )
        return schema
    },
    export: () => '',
    image:
        'data:image/png;base64,',
    initialValues: {
        'address-line-1': {
            ...TextInput.initialValues,
            question: 'Address line 1',
            grid: 12
        },
        'address-line-2': {
            ...TextInput.initialValues,
            question: 'Address line 2',
            required: false,
            grid: 12
        },
        city: {
            ...TextInput.initialValues,
            question: 'City',
            grid: 6
        },
        state: {
            ...TextInput.initialValues,
            question: 'State',
            grid: 6
        },
        postal: {
            ...TextInput.initialValues,
            question: 'Postal code',
            grid: 6
        },
        country: {
            ...Country.initialValues,
            question: 'Country',
            formKey: '',
            grid: 6
        }
    },
    settings: [
        {
            id: 'address-line-1',
            label: <Trans>Address line 1</Trans>,
            type: 'form-elements',
            settings: TextInput.settings
        },
        {
            id: 'address-line-2',
            label: <Trans>Address line 2</Trans>,
            type: 'form-elements',
            settings: TextInput.settings
        },
        {
            id: 'city',
            label: <Trans>City</Trans>,
            type: 'form-elements',
            settings: TextInput.settings
        },
        {
            id: 'state',
            label: <Trans>State</Trans>,
            type: 'form-elements',
            settings: TextInput.settings
        },
        {
            id: 'postal',
            label: <Trans>Postal code</Trans>,
            type: 'form-elements',
            settings: TextInput.settings
        },
        {
            id: 'country',
            label: <Trans>Country</Trans>,
            type: 'form-elements',
            settings: Country.settings
        }
    ],
    type: 'block'
} as DndBlockItem
